import { createSlice } from "@reduxjs/toolkit";

const userSlice=createSlice({
    name:'user',
    initialState:{
        isRentant:true,
        isAuthenticated:false,
        userName:"Login"
    },
    reducers:{
        updateIsAuthenticated:(state)=>state.isAuthenticated=true,

        updateIsRentant:(state)=>state.isRentant=!state.isRentant,

        updateUserName:(state,action)=>state.userName=action.payload
    }
});

export const {updateIsAuthenticated,updateIsRentant,updateUserName} = userSlice.actions;
export default userSlice.reducer;
