import React, { useEffect, useState } from 'react';
import style from './App.module.css';
import { updateIsAuthenticated, updateIsRentant,updateUserName } from './app/userSlice';
import { useDispatch, useSelector } from 'react-redux';
import Navbar from './components/Navbar';
import Post from './components/Post';
import MyPosts from './components/MyPosts';
import ApplyFilter from './components/ApplyFilter';
import PostProperty from './components/PostProperty';
import LoginForm from './components/LoginForm';
import SignUpForm from './components/SignUpForm';

const App = () => {

  const [properties, updateProperties] = useState([]);
  const [postedProperties, updatePostedProperties] = useState();
  const [AuthenticationForm, setAuthenticationForm] = useState(false);
  const [userExist, updateUserExist] = useState(true);
  const [loginData,updateLoginData]=useState({email:"",password:""});

  const isRentant = useSelector(state => state.user.isRentant);
  const isAuthenticated = useSelector(state => state.user.isAuthenticated);
  const userName = useSelector(state => state.user.userName);




  const getFeedPosts = async () => {
    const result = await fetch('/all-posts');
    const user=await result.json();
    updateProperties(user.data);
  }

  const getPostedProperties = async () => {
    const result = await fetch(`/${loginData.email}/posts`);
    const userPosts=result.json();
    updatePostedProperties(userPosts);
  }

  const isUserValid = async()=>{
    const result= await fetch('/users',{
      method:"POST",
      body:JSON.stringify({
        email:loginData.email,
        password:loginData.password
      }),
      headers:{
        "content-type": "application/json"
      }
    });
    return result;
  }



  const deletePostHandler=async(id)=>{
    const result=await fetch('/remove-post',{
      method:"DELETE",
      body:JSON.stringify({id:id,email:loginData.email}),
      headers:{
        "content-type": "application/json"
      }
    })
  }

  useEffect(() => {
    if (isRentant)
      getFeedPosts();
    else
      getPostedProperties();
  }, [isRentant])


  const dispatch = useDispatch();

  const toggleIntentionHandler = () => {
    dispatch(updateIsRentant());
  }

  const showFormHandler = () => {
    if(!isAuthenticated)
      setAuthenticationForm(true);
  }

  const signUpFormSubmitHandler = async(name) => {
      setAuthenticationForm(false);
      dispatch(updateIsAuthenticated());
      dispatch(updateUserName(name));
  }

  const loginFormSubmitHandler = async(event) => {
    event.preventDefault();
    const name=await isUserValid();
    if(name.length>0){
      setAuthenticationForm(false);
      dispatch(updateIsAuthenticated());
      dispatch(updateUserName(name));
    }
  }



  return (
    <div className={style['app-container']}>

      {AuthenticationForm && (userExist ?
        <LoginForm 
          onSignUpButtonClick={() => updateUserExist(false)} 
          onSubmit={loginFormSubmitHandler} 
          onInputChange={(e)=>updateLoginData((prevData)=>({...prevData,[e.target.name]:e.target.value}))}
        /> :
        <SignUpForm 
          onSubmit={signUpFormSubmitHandler} 
        />
      )}

      <Navbar 
        userName={userName}
        onLoginButtonClick={showFormHandler}
        toggleIntention={toggleIntentionHandler} 
        buttonText={isRentant ? "My Property" : "Rent"} 
      />


      {isRentant &&
        <div className={style['post-properties-container']}>
          {/* <ApplyFilter></ApplyFilter> */}
          <div className={style['post-properties']}>
            {properties.map((property) => {
              return property.posts.map((userPost)=>
                <Post
                  key={userPost._id} 
                  postData={userPost}
                  userData={property} 
                  showForm={showFormHandler} 
                />
              )}
            )}
          </div>
        </div>
      }


      {!isRentant &&
        <div className={style['my-posts-container']}>
          <PostProperty></PostProperty>
          <div className={style['my-posts']}>
            {postedProperties.map((property, ind) => {
              <Post 
                key={ind} 
                data={property} 
                deletePost={deletePostHandler}
              />
            })}
          </div>
        </div>
      }
    </div>
  );
};

export default App;
