import React,{useState} from 'react';
import style from './Post.module.css';
import { useSelector} from 'react-redux';
import { FaHeart } from "react-icons/fa";


const Posts = (props) => {
    const [userDetails,setUserDetails]=useState(false);

    const isRentant=useSelector(state=>state.user.isRentant);
    const isAuthenticated=useSelector(state=>state.user.isAuthenticated);

    const interestedButtonHandler=()=>{
        if(!isAuthenticated)
            props.showForm();
        else
            setUserDetails((prev)=>prev=!prev);
    }

    const toggleLikeHandler=async()=>{
        await fetch('/users/posts/likes')
    }

    return (
        <div className={style['posted-property-container']}>
            <div className={style['post-likes-container']}>
            <div className={style['likes']}>
                {props.postData.likes} <FaHeart 
                    onClick={toggleLikeHandler} 
                    className={`${style['like-icon']} ${props.isLiked && style['is-liked']}`}
                />
            </div>
            </div>
            <div className={style['price']}>{props.postData.price}</div>
            <div className={style['desc']}>{props.postData.desc}</div>
            <div>No. of Bedrooms:{props.postData.bedrooms}</div>
            <div>No. of Bathrooms:{props.postData.bathroom}</div>
            <div>size:{props.postData.areaSize}</div>
            <div className={style['post-address-container']}>
                <div>{props.postData.address.city}</div>
                <div>{props.postData.address.area}</div>
                <div>{props.postData.address.pincode}</div>
            </div>

            {userDetails &&
                <div className={style['user-details']}>
                    <div>{props.userData.name}</div>
                    <div>{props.userData.lastName}</div>
                    <div>{props.userData.email}</div>
                    <div>{props.userData.mobile}</div>
                </div>
            }

            {isRentant ?
                <button 
                    onClick={interestedButtonHandler} 
                    className={style['i-am-interested-button']}>
                        {userDetails?"Show Less":"I am Interested"}
                </button>:
                <button 
                    onClick={()=>props.deletePost(props.postData._id)}
                    className={style['Delete-Post-button']}>
                        Delete
                </button>
            }   
        </div>
    );
};

export default Posts;
